YDL_OPTS_YOUTUBE = {
    'format': 'best[ext=mp4]/best',
    'outtmpl': 'video_%(id)s.%(ext)s',
    'quiet': True,
    'no_warnings': True,
    'cookiefile': 'cookies.txt'
}

YDL_OPTS_PINTEREST = {
    'format': 'best',
    'outtmpl': 'video_%(id)s.%(ext)s',
    'quiet': True,
    'no_warnings': True
}

YDL_OPTS_DEFAULT = {
    'format': 'best',
    'outtmpl': 'video_%(id)s.%(ext)s',
    'quiet': True,
    'no_warnings': True,
    'cookiefile': 'cookies.txt'
}

def extract_metadata_from_info(info):
    width = info.get("width")
    height = info.get("height")
    duration = info.get("duration")
    if not width or not height:
        formats = info.get("formats") or []
        for f in formats:
            if f.get("width") and f.get("height"):
                width = f.get("width")
                height = f.get("height")
                break
    return width, height, duration

def download_thumbnail(url, target_path):
    try:
        resp = requests.get(url, stream=True, timeout=15)
        if resp.status_code == 200:
            with open(target_path, "wb") as f:
                for chunk in resp.iter_content(chunk_size=8192):
                    if chunk:
                        f.write(chunk)
            if os.path.exists(target_path):
                return target_path
    except Exception:
        pass
    return None

def pick_opts(url):
    if "youtube.com" in url or "youtu.be" in url:
        return YDL_OPTS_YOUTUBE
    if "pinterest.com" in url:
        return YDL_OPTS_PINTEREST
    return YDL_OPTS_DEFAULT

@app.on_message(filters.command("start") & filters.private)
async def start(client, message):
    await message.reply_text("""Welcome üëã

This bot lets you download videos from
YouTube, TikTok, Instagram, and more.

 üíé devloper by @laki3012

üëâ Just send the video link""", quote=True)

@app.on_message(filters.private & filters.text)
async def handler(client, message):
    url = message.text.strip()
    if not url.startswith(("http://", "https://")):
        return

    msg = await message.reply_text("‚è≥ Processing...", quote=True)

    try:
        loop = asyncio.get_event_loop()
        ydl_opts = pick_opts(url)

        with YoutubeDL(ydl_opts) as ydl:
            info = await loop.run_in_executor(None, lambda: ydl.extract_info(url, download=False))

            duration = info.get("duration")
            if duration and duration > MAX_DURATION:
                await msg.edit("Zxp masoo dajin kari Video ka dheer 30 daqiiqo. Beta ayaan ku jiraa.")
                return

            info = await loop.run_in_executor(None, lambda: ydl.extract_info(url, download=True))
            filename = ydl.prepare_filename(info)

        await msg.edit_text("üì§ Uploading...")

        caption = info.get('title', 'Video')
        if info.get('description'):
            caption = info.get('description')[:1024]

        width, height, duration = extract_metadata_from_info(info)

        thumb_path = None
        thumb_url = info.get("thumbnail")
        if thumb_url:
            temp_thumb = f"thumb_{info.get('id')}.jpg"
            thumb_path = download_thumbnail(thumb_url, temp_thumb)

        await app.send_video(
            chat_id=message.chat.id,
            video=filename,
            caption=caption,
            thumb=thumb_path,
            width=width if width else 0,
            height=height if height else 0,
            duration=int(duration) if duration else 0,
            reply_to_message_id=message.id
        )

        if os.path.exists(filename):
            os.remove(filename)
        if thumb_path and os.path.exists(thumb_path):
            os.remove(thumb_path)

        await msg.delete()

    except Exception as e:
        error_message = f"‚ùå Error: {str(e)}"
        await msg.edit(error_message[:4096])

if __name__ == "__main__":
    app.run()
